import React, { ChangeEvent, useState } from "react";
import "./App.css";
import { ITask } from "./interface";
import Todo from "./Todo";

function App() {
  const [todo, setTodo] = useState<string>("");
  const [deadline, setDeadline] = useState<number>(0);
  const [todoList, setTodoList] = useState<ITask[]>([]);

  const handleChange = (e: ChangeEvent<HTMLInputElement>): void => {
    if (e.target.name === "todo") {
      console.log(todo);
      setTodo(e.target.value);
    }
    if (e.target.name === "deadline") {
      setDeadline(Number(e.target.value));
    }
  };

  const handleSubmit = (): void => {
    const newTodo = { todoName: todo, deadline: deadline };
    setTodoList([...todoList, newTodo]);
    setTodo("");
    setDeadline(0);
  };

  const handleDelete = (delTodo: string): void => {
    setTodoList(
      todoList.filter((todo) => {
        return todo.todoName !== delTodo;
      })
    );
  };

  return (
    <div className="App">
      <div>
        <input
          type="text"
          placeholder="todo"
          name="todo"
          value={todo}
          onChange={handleChange}
        />
        <input
          type="number"
          placeholder="deadline"
          name="deadline"
          value={deadline}
          onChange={handleChange}
        />
        <button onClick={handleSubmit}>추가</button>
      </div>
      <div className="todoList">
        {todoList.map((todo: ITask, key: number) => {
          return <Todo key={key} todo={todo} handleDelete={handleDelete} />;
        })}
      </div>
    </div>
  );
}

export default App;
